mapper.select:
        SELECT

        FROM cus_customer a

			JOIN info_plan p ON a.plan = p.id

        JOIN sys_business b ON  a.business = b.id
        WHERE a.delflag = #{DEL_FLAG_NORMAL}
		AND a.duration = #{duration}
		AND a.business = #{user.business.id}

            AND a.name = #{name}


            AND a.telephone = #{telephone}

SELECT

FROM cus_customer a

	JOIN info_plan p ON a.plan = p.id

JOIN sys_business b ON  a.business = b.id
WHERE a.delflag = #{DEL_FLAG_NORMAL}
AND a.duration = #{duration}

	AND a.business = #{user.business.id}

AND a.id = #{id}
